    private void BotEnvHoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotEnvHoActionPerformed
        network = new Network(Integer.parseInt(TextHost.getText()));
        clase.setText(network.getClassType());
        Red.setText(network.getClassMainAddress());
    }//GEN-LAST:event_BotEnvHoActionPerformed

