    private void BotEnvActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotEnvActionPerformed
        System.out.println("TRATANDO DE CREAR SUBRED");
        boolean flag = network.AddSubNetwork(Integer.parseInt(jTextField2.getText()),jComboBox1.getSelectedItem().toString());
        System.out.println("SUBRED CREADA");
        subnetwork = network.getLastSubNetwork();
        
        DefaultTableModel model = (DefaultTableModel)jTable1.getModel();
        if(flag)
            model.addRow(new Object []{jTextField1.getText(),subnetwork.getSubNetMask(),subnetwork.getSubNetworkAddress(),subnetwork.getFirstAddress(),subnetwork.getLastAddress(),subnetwork.getGateway(),subnetwork.getBroadcast()});

        jTextField1.setText("");
        jTextField2.setText("");
        
    }//GEN-LAST:event_BotEnvActionPerformed

    private void Borrar_todoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Borrar_todoActionPerformed
        DefaultTableModel model = (DefaultTableModel)jTable1.getModel();
        System.out.println(model.getRowCount());
        while(model.getRowCount()>0){
            model.removeRow(0);
        }
        
        jComboBox1.setSelectedIndex(0);
        TextHost.setText("");
        Red.setText("");
        clase.setText("");
    }//GEN-LAST:event_Borrar_todoActionPerformed

